import React, { Component } from 'react';
import { Col, Button } from "reactstrap";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';

class ServiceBox extends Component {
    render() {
        let button;
        // if (service.button) {
        //   button = <Button color="success" className="btn-rounded navbar-btn">{service.button}</Button>;
        // }
        // else {
        //     button = null;
        // }

        return (
            
            <React.Fragment>
                {
                    this.props.services.map((service, key) =>
                    
{                    
                        if(service.button){
                            button = <Button color="success" className="btn-rounded navbar-btn">{service.button}</Button>
                        }   
                        <Col xl={4} sm={6} key={key} >
                        <div className="text-center p-4 mt-3">
                            <div className="avatar-md mx-auto mb-4">
                                <span className="avatar-title rounded-circle bg-soft-primary">
                                    <i>
                                    <FontAwesomeIcon icon={service.icon} className="icon-dual-primary" />
                                    </i>
                                </span>
                            </div>
                            <h5 className="font-18">{service.title}</h5>
                            <p className="mb-0">{service.desc}</p>
                            <br/>
                            {button}
                        </div>
                    </Col>}
                    )
                }        
            </React.Fragment>
        );
    }
}

export default ServiceBox;
