import React, { Component } from 'react';
import { Container, Row, Col } from "reactstrap";
import { Link } from "react-router-dom";

//Import Section Title
import SectionTitle from "../common/section-title";
import ServiceBox from "./services-box";

class Process extends Component {
    constructor(props) {
        super(props);
        this.state = {
            services : [
                { icon : "coffee", title : "Manger", desc : "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris mattis fermentum magna eu tristique. Nulla in est arcu. In vulputate metus nibh, ut scelerisque urna vehicula non. Pellentesque vel aliquet nisl. Phasellus non nisi sit amet risus pharetra condimentum." },
                { icon : "edit", title : "Jeux-vidéos", desc : "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris mattis fermentum magna eu tristique. Nulla in est arcu. In vulputate metus nibh, ut scelerisque urna vehicula non. Pellentesque vel aliquet nisl. Phasellus non nisi sit amet risus pharetra condimentum." },
                { icon : "headphones", title : "Jeux de société", desc : "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris mattis fermentum magna eu tristique. Nulla in est arcu. In vulputate metus nibh, ut scelerisque urna vehicula non. Pellentesque vel aliquet nisl. Phasellus non nisi sit amet risus pharetra condimentum." },
                { icon : "layers", title : "Travailler", desc : "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris mattis fermentum magna eu tristique. Nulla in est arcu. In vulputate metus nibh, ut scelerisque urna vehicula non. Pellentesque vel aliquet nisl. Phasellus non nisi sit amet risus pharetra condimentum." },
                { icon : "code", title : "Se détendre", desc : "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris mattis fermentum magna eu tristique. Nulla in est arcu. In vulputate metus nibh, ut scelerisque urna vehicula non. Pellentesque vel aliquet nisl. Phasellus non nisi sit amet risus pharetra condimentum." },
                { icon : "drink", title : "Boire", desc : "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris mattis fermentum magna eu tristique. Nulla in est arcu. In vulputate metus nibh, ut scelerisque urna vehicula non. Pellentesque vel aliquet nisl. Phasellus non nisi sit amet risus pharetra condimentum." },
            ]
        }
    }
    
    render() {
        return (
            <React.Fragment>
        <section className="section bg-light" id="services">
            <Container>
            <SectionTitle
                title="Qu'est-ce qu'on peut faire au Pic ?"
                desc="Le Pic propose une myriade d'activités à faire, seul ou en groupe, à toutes les heures de la journée.
                C'est un endroit de détente où il est simple de rencontrer de nouvelles personnes et passer du bon temps avec celles qu'on connaît déjà."
                extraTag="animTitle"
            />

                <Row>
                    <ServiceBox services={this.state.services} />
                </Row>
                

                <Row className="mt-4">
                    <Col lg={12}>
                        <div className="text-center">
                            <Link to="#" className="btn btn-success">View more</Link>
                        </div>
                    </Col>
                </Row>
                
            </Container>
            
        </section>
            </React.Fragment>
        );
    }
}

export default Process;
